import React from 'react';
import PageNavbar from './PageNavbar';
import RecommendationsRow from './RecommendationsRow';
import '../style/Recommendations.css';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class Recommendations extends React.Component {

	constructor(props) {
        super(props);

		const eventsArr = [
		    //按钮点击事件
		    "onSignIn",
		    //user name输入改变
		    "onChangeName",
		    //password输入改变
		    "onChangePwd"
		];
        //一次性绑定所有事件，这样以后添加的事件只要添加到eventArr数组里面就好了
        eventsArr.map((ev) => {
            this[ev] = this[ev].bind(this);
            return "";
        });

        this.state = {
            name: '',
            pwd: '',
            errorMsg:''
        };
    }




    handleChange(key, val) {
        this.setState({
            [key]: val
        })
    }

    handleSubmit(e) {
        e.preventDefault();

        console.log(this.state);
    }



    render() {
        const { username, password } = this.state;
        return (
            <div className="sign-in">
                <h2>I already have an account</h2>
                <span>Sign in with your email and passowrd</span>

                <form onSubmit={this.handleSubmit}>
               	<div className='group'>
                  <input 
                  		className='form-input' 
                        required
                        onChange={this.handleInput(username, password)}
                 />
                  <label
                  	className={`${password.length?'shrink':''} form-input-label`}
                  >Email</label>
                 <input 
                		className='form-input' 
                        onChange={this.handleInput(username, password)}
                        required
                 />
                  <label
                  className={`${password.length?'shrink':''} form-input-label`}
                  >Password</label>
                  </div>
                   <button className='custom-button' type='submit' >Sign in</button>
                </form>
            </div>
        )
    }
}
