import PageNavbar from './PageNavbar';
import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';
import { withRouter } from 'react-router-dom'
import '../style/SignIn.css';



class SignIn extends React.Component {

  constructor(props) {
    super(props);
    this.state = {username: "",password:""};

	const eventsArr = [
		    //按钮点击事件
		    "onSignIn",
		    //user name输入改变
		    "onChangeName",
		    //password输入改变
		    "onChangePwd"
		];
        //一次性绑定所有事件，这样以后添加的事件只要添加到eventArr数组里面就好了
        eventsArr.map((ev) => {
            this[ev] = this[ev].bind(this);
            return "";
        });

  }

	onChangeName(e) {
        this.setState({ username: e.target.value });
        this.setState({ errorMsg: '' });
    }

    onChangePwd(e) {
        this.setState({ password: e.target.value });
    }

  onSignIn(event) {
    // Send an HTTP request to the server.
    console.log("http://localhost:8081/signin?username="+this.state.username+"&password="+this.state.password)
    console.log(this.state.username)
    console.log(this.state.password)

    fetch("http://localhost:8081/signin?username="+this.state.username+"&password="+this.state.password, {
      //mode: "no-cors",
      method: 'GET' // The type of HTTP request.

     })
       .then(res => res.json()) // Convert the response data to a JSON.
       .then(ifexist => {
        console.log("success", ifexist);

      //   if (!ifexist) alert("Invalid account");
      // alert("Invalid account")
   	 	// this.props.history.push({
      //                   pathname : '/dashboard',
      //                   state :{
      //                       username:this.state.username
      //                   }
      //               });

       })
       .catch(err => console.log(err)) // Print the error if there is one.
    }

  render() {
    return (
    	<div className="SignIn">

        <PageNavbar active="signin" />

        <br></br>

        <div className="small-middle-container">
      <form onSubmit={this.onSignIn}>


        <div className="form-group">
          <label>Username</label>
          <input value={this.state.username} onChange={this.onChangeName} className="form-control"/>
        </div>

        <div className="form-group">
          <label>Password</label>
          <input type="password" className="form-control" id="exampleInputPassword1" value={this.state.password}
           onChange={this.onChangePwd}/>
        </div>

        <button type="submit" className="btn btn-primary">Submit</button>


      </form></div>

      </div>  

    );
  }
}
export default withRouter(SignIn);
