import PageNavbar from './PageNavbar';
import RecommendationsRow from './RecommendationsRow';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from 'react';
import { withRouter } from 'react-router-dom'
//import { TextField } from 'office-ui-fabric-react/lib/TextField';
//import { DefaultButton } from 'office-ui-fabric-react/lib/Button';
//import { Container, Row, Col } from 'reactstrap';
import '../style/Recommendations.css';
import GenreButton from './GenreButton';



export default class Recommendations extends React.Component {

  constructor(props) {
    super(props);
    this.state = {username: '',password:''};

	const eventsArr = [
		    //按钮点击事件
		    "onSignIn",
		    //user name输入改变
		    "onChangeName",
		    //password输入改变
		    "onChangePwd"
		];
        //一次性绑定所有事件，这样以后添加的事件只要添加到eventArr数组里面就好了
        eventsArr.map((ev) => {
            this[ev] = this[ev].bind(this);
            return "";
        });

  }

	onChangeName(event, value) {
        this.setState({ username: value });
        this.setState({ errorMsg: '' });
    }

    onChangePwd(event, value) {
        this.setState({ password: value });
    }

  onSignIn(event) {
    console.log('A name was submitted: ' + this.state.username);
    event.preventDefault();
  }

  render() {
    return (
    	<div className="Recommendations">

        <PageNavbar active="recommendations" />

        <br></br>

    	<div className="signin-body">

    	<div className="signin-content">

      <form onSubmit={this.onSignIn}>
        <label>
        <div>
          <input type="text" value={this.state.username} onChange={this.onChangeName} /></div>
                <div margin = "6px">
  <input type="text" value={this.state.password} onChange={this.onChangePwd} />        </div>




        </label>
        <input type="submit" value="Submit" />
      </form></div>

      </div>    </div>

    );
  }
}
