import PageNavbar from './PageNavbar';
import RecommendationsRow from './RecommendationsRow';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from 'react';
import { withRouter } from 'react-router-dom'
//import { TextField } from 'office-ui-fabric-react/lib/TextField';
//import { DefaultButton } from 'office-ui-fabric-react/lib/Button';
//import { Container, Row, Col } from 'reactstrap';
import '../style/Recommendations.css';
import GenreButton from './GenreButton';



export default class Recommendations extends React.Component {

	constructor(props) {
        super(props);

		const eventsArr = [
		    //按钮点击事件
		    "onSignIn",
		    //user name输入改变
		    "onChangeName",
		    //password输入改变
		    "onChangePwd"
		];
        //一次性绑定所有事件，这样以后添加的事件只要添加到eventArr数组里面就好了
        eventsArr.map((ev) => {
            this[ev] = this[ev].bind(this);
            return "";
        });

        this.state = {
            name: '',
            pwd: '',
            errorMsg:''
        };
    }

	onSignIn() {
		const owners = [
	    { name: "demo", pwd: "123456" },
	    { name: "oliver",pwd: "123456"}
		]
        var authorizd = false;
        //这里做个简单的用户登录验证，后面可以通过数据库读取数据来验证
        owners.map((value, index) => {
            if (this.state.name === value.name && this.state.pwd === value.pwd) {                
                authorizd = true;
            }
        });
        if (authorizd) {
            //跳转到主页面
            this.props.history.push("/dashboard");            
        }
        else {
            this.setState({
                errorMsg: "Incorrect username or password."
            });
        }
    }


	onChangeName(event, value) {
        this.setState({ name: value });
        this.setState({ errorMsg: '' });
    }

    onChangePwd(event, value) {
        this.setState({ pwd: value });
    }



render() {
        return (
            <div className="signin-body">
                        <div className="signin-title">
                            Sign in to Oliver Demo
                        </div>
                        <div className="signin-content">
                        <div>
                            <input label="User Name" Value={this.state.name} errorMessage={this.state.errorMsg} onChange={this.onChangeName} />
                            </div>
                            <input label="Password" value={this.state.pwd} type="Password" onChange={this.onChangePwd} />
                            <button className="signin-button" onClick={this.onSignIn} Text="Sign In"/>
                        </div>
            </div>
        );
    }

}
