    //       <GenreButton id={"button-" + genreObj.genre} onClick={() => this.showMovies(genreObj.genre)} genre={genreObj.genre} />
    //     );

    //     // Set the state of the genres list to the value returned by the HTTP response from the server.
    //     this.setState({
    //       genres: genreDivs
    //     })
    //   })
    //   .catch(err => console.log(err))	// Print the error if there is one.

  onStarClick(nextValue, prevValue, name) {
    this.setState({curRating: nextValue});
  }

  sumbitRating() {    
    if (!this.state.username) {
      alert("Please log in first!");
    } else if (this.state.curRating == 0) {
      alert("Please select a number!")
    } else if (!this.state.ISBN) {
      alert("Error");
    }

    //if no previous rating exists in db, insertion query
    if (!this.state.prevRating) {
      fetch(`http://localhost:8081/submitRating/${this.state.username}/${this.state.ISBN}/${this.state.curRating}`, {
        method: 'GET'
      })
        .then(res => res.json())
        .then(res => {
          //res is {status: "success"} if server -> db is sucessful
          if (res.status == "success") {
            this.setState({prevRating: true});
            alert("Rating submitted");
          } else {
            alert("DB Query Error");
          }
        })
        .catch(err => console.log(err));

    //If exists previous rating in db, UPDATE query
    } else { 
      fetch(`http://localhost:8081/updateRating/${this.state.username}/${this.state.ISBN}/${this.state.curRating}`, {
        method: 'GET'
      })
        .then(res => res.json())
        .then(res => {
          //res is {status: "success"} if server -> db is sucessful
          if (res.status == "success") {
            alert("Rating updataed");
          } else {
            alert("DB Query Error");
          }
        })
        .catch(err => console.log(err));
    }

  }

  getRelatedBooks(ISBN) {
    fetch(`http://localhost:8081/relatedBooks/${this.state.ISBN}`, {
      method: 'GET' 
    })
      .then(res => res.json())
      .then(resArray => {
        console.log("aaaa");
        console.log(resArray);
        if (!resArray) return;
        this.setState({
          relatedBooks: resArray
        });
      })
      .catch(err => console.log(err));

    console.log("relatedBooks, array");
    console.log(this.state.relatedBooks);
  }

  //合的时候用zz写的!!!!!
  addToBookshelf(ISBN) {
  }

  render() {  
    //const {curRating} = this.state;  
    const responsive = {
      desktop: {
        breakpoint: { max: 3000, min: 1024 },
        items: 3,
        slidesToSlide: 3 // optional, default to 1.
      },
      tablet: {
        breakpoint: { max: 1024, min: 464 },
        items: 2,
        slidesToSlide: 2 // optional, default to 1.
      },
      mobile: {
        breakpoint: { max: 464, min: 0 },
        items: 1,
        slidesToSlide: 1 // optional, default to 1.
      }
    };

    return (
        <div className="Subpage">
          <PageNavbar active="" />
          <br></br>
          <div className="container movies-container mt-5">
            <div className="list-wrap">
              <div className="list pr-1">
                <div className="list-box bg-white px-3 pt-3">


          {/* <!-- Page Content --> */}
          <h5 className="list-title mb-3">≡ Book Information</h5>

          {/* <!-- Book Info Row --> */}
          <div className="list-row" >
            <div className="col">
              <img className="img-fluid" src={this.state.book.image_url_l} alt=""/>
            </div>

            <div className="col">
              <h3 className="my-4">{this.state.book.title}</h3>
              <div className="row ratingRow1">
                {/* <img id="starIcon" src={StarIcon}/>
                <h6>rating</h6> */}
                <p>Average rating: </p>
                <p>{this.state.avgRating.toFixed(2)}</p>
              </div>
              <div className="row ratingRow2">
                <p>Your rating: </p>
                <StarRatingComponent 
                          name="rate2"
                          editing={true}
                          starCount={10}
                          value={this.state.curRating}
                          onStarClick={this.onStarClick.bind(this)}
                />
                <p>{this.state.curRating}</p>
              </div>
              <button
                  className="btn btn-outline-secondary bg-primary text-white"
                  type="button"
                  id="button-rating"
                  onClick={this.sumbitRating}>
                  Submit Your Rating
              </button>
              {/* <h3 className="my-3">Book Description</h3> */}
              <ul>
                <li>ISBN: {this.state.ISBN}</li>
                <li>Author: {this.state.book.author}</li>
                <li>Publisher: {this.state.book.publisher}</li>
                <li>Year: {this.state.book.year}</li>
              </ul>
              <button
                  className="btn btn-outline-secondary bg-primary text-white"
                  type="button"
                  id="button-markBook"
                  onClick={this.addToBookshelf}>
                  Like it? Add to bookshelf
              </button>
            </div>       
          </div>
          {/* <!-- /.row --> */}  


          <h3 className="my-4">Related Books</h3>
          {/* <!-- Related Books Row --> */}        
          {/* <div className="list-row">
            {function(this.state.relatedBooks}
            }
            <div className="col-md-3 col-sm-6 mb-4">
              <a href="#">
                    <img className="img-fluid" src="http://placehold.it/500x300" alt=""/>
                </a>
            </div>

            <div className="col-md-3 col-sm-6 mb-4">
              <a href="#">
                    <img className="img-fluid" src="http://placehold.it/500x300" alt=""/>
                  </a>
            </div>

            <div className="col-md-3 col-sm-6 mb-4">
              <a href="#">
                    <img className="img-fluid" src="http://placehold.it/500x300" alt="" />
                  </a>
            </div>

            <div className="col-md-3 col-sm-6 mb-4">
              <a href="#">
                    <img className="img-fluid" src="http://placehold.it/500x300" alt="" />
                  </a>
            </div>

          </div> */}
          {/* <!-- /.row --> */}  

          <Carousel
            swipeable={false}
            draggable={false}
            showDots={true}
            responsive={responsive}
            ssr={true} // means to render carousel on server-side.
            infinite={true}
            autoPlay={this.props.deviceType !== "mobile" ? true : false}
            autoPlaySpeed={3000}
            keyBoardControl={true}
            customTransition="all .5"
            transitionDuration={500}
            containerClass="carousel-container"
            removeArrowOnDeviceType={["tablet", "mobile"]}
            deviceType={this.props.deviceType}
            dotListClass="custom-dot-list-style"
            itemClass="carousel-item-padding-40-px"
          >
            {this.state.relatedBooks.map((element, index) => {
              return (
                  <div className="col-md-3 col-sm-6 mb-4" key={index}>
                    <div className="card">
                      <img className="card-img-top" src={element.image_url_l} />
                    </div>
                  </div>
              );
            })}
          </Carousel>


        </div>
        </div>
        </div>
        </div>
        </div>
    );
  }
