import PageNavbar from './PageNavbar';
import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';
import { withRouter } from 'react-router-dom'
import '../style/SignIn.css';



class SignIn extends React.Component {

  constructor(props) {
    super(props);
    this.state = {username: "",password:""};
    this.owners = [
    { name: "demo", pwd: "123456" },
    { name: "oliver",pwd: "123456"}]
    const eventsArr = [
		    //按钮点击事件
		    "onLogin",
		    //user name输入改变
		    "onChangeName",
		    //password输入改变
		    "onChangePwd",
        "onSignUp"
        ];
        //一次性绑定所有事件，这样以后添加的事件只要添加到eventArr数组里面就好了
        eventsArr.map((ev) => {
          this[ev] = this[ev].bind(this);
          return "";
        });

      }

      onChangeName(e) {
        this.setState({ username: e.target.value });
        this.setState({ errorMsg: '' });
      }

      onChangePwd(e) {
        this.setState({ password: e.target.value });
      }

      onLogin(event) {
    // Send an HTTP request to the server.

  //   fetch("http://localhost:8081/signin?username="+this.state.username+"&password="+this.state.password, {
  //     method: 'GET', // The type of HTTP request.
  //    }).then(function(response) {return response.json();alert("12321")}).then(function(myJson) {
  //   alert(myJson);
  // })



  var authorizd = false;

  this.owners.map((value, index) => {
    if (this.state.username === value.name && this.state.password === value.pwd) {                
      authorizd = true;
    }
  });
  alert(authorizd)
  if (authorizd){
    this.props.history.push({
      pathname : '/dashboard',
      state :{
        username:this.state.username
      }
    });
  }

       // .then(res => res.json()) // Convert the response data to a JSON.
       // .then(res => {
       //  console.log("success", res);

      //   if (!ifexist) alert("Invalid account");
      // alert("Invalid account")
   	 	// this.props.history.push({
      //                   pathname : '/dashboard',
      //                   state :{
      //                       username:this.state.username
      //                   }
      //               });

       //})
       //.catch(err => console.log(err)) // Print the error if there is one.
     }

     onSignUp(event){
      alert(this.state.username+this.state.password);
  //   fetch("http://localhost:8081/signup?username="+this.state.username+"&password="+this.state.password, {
  //     method: 'POST', // The type of HTTP request.
  //    }).then(function(response) {return response.json();alert("12321")}).then(function(myJson) {
  //   alert(myJson);
  // })
      var authorizd = true;
      this.owners.map((value, index) => {
        if (this.state.username === value.name) {                
            authorizd = false;
            alert("username already exists");
        }
      });
      if (authorizd ){
          this.owners.push({ name: this.state.username, pwd: this.state.password })
      }
}

render() {
  return (
   <div className="SignIn">

   <PageNavbar active="signin" />

   <br></br>

   <div className="small-middle-container">


   <div className="form-group">
   <label>Username</label>
   <input value={this.state.username} onChange={this.onChangeName} className="form-control"/>
   </div>

   <div className="form-group">
   <label>Password</label>
   <input type="password" className="form-control" id="exampleInputPassword1" value={this.state.password}
   onChange={this.onChangePwd}/>
   </div>

   <button type="submit" className="btn btn-primary" onClick={this.onLogin}>LogIn</button>&nbsp;&nbsp;&nbsp;
   <button className="btn btn-primary" onClick={this.onSignUp}>Sign Up</button>


   </div>

   </div>  

   );
 }
}
export default withRouter(SignIn);
