import React from 'react';
import PageNavbar from './PageNavbar';
import RecommendationsRow from './RecommendationsRow';
import '../style/Recommendations.css';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class Recommendations extends React.Component {
	constructor(props) {
		super(props);

		// State maintained by this React component is the selected movie name,
		// and the list of recommended movies.
		this.state = {
			username: '',
			password: ''
		}

		this.handleMovieNameChange = this.handleMovieNameChange.bind(this);
		this.submitMovie = this.submitMovie.bind(this);
	}

	handleMovieNameChange(e) {
		this.setState({
			movieName: e.target.value
		});
	}

	/* ---- Q2 (Recommendations) ---- */
	// Hint: Name of movie submitted is contained in `this.state.movieName`.
	submitMovie() {
		fetch("http://localhost:8081/recommendations/"+this.state.movieName, {
			method: "GET"
		})
			.then(res => res.json())
			.then(movielist => {
				console.log(movielist); //displays your JSON object in the console
				let movieDivs = movielist.map((movie, i) => 
					<RecommendationsRow key={i} title={movie.title} id ={movie.id} rating={movie.rating} vote_count = {movie.vote_count}></RecommendationsRow>
				);



				//This saves our HTML representation of the data into the state, which we can call in our render function
				this.setState({
					recMovies: movieDivs
				});
			})
			.catch(err => console.log(err))		
	}


	render() {

		return (
			<div className="Recommendations">
				<PageNavbar active="recommendations" />

			    <div className="container recommendations-container">
			    	<div className="jumbotron">
			    		<div className="h5">Recommendations</div>
			    		<br></br>
			    		<div className="input-container">
			    			<input type='text' placeholder="Enter Username" value={this.state.userName} onChange={this.handleUsernameChange} id="userName" className="username-input"/>
			    			<button id="submitMovieBtn" className="submit-btn" onClick={this.submitMovie}>Submit</button>
			    		</div>
			    		<div className="input-container">
			    			<input type='text' placeholder="Enter password" value={this.state.movieName} onChange={this.handleMovieNameChange} id="password" className="password-input"/>
			    			<button id="submitMovieBtn" className="submit-btn" onClick={this.submitMovie}>Submit</button>
			    		</div>
			    	</div>
			    </div>
		    </div>
		);
	}
}
