import React from 'react';
import { withRouter } from "react-router-dom";
import '../style/Subpage.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import PageNavbar from './PageNavbar';
import StarIcon from "../assets/images/star.png";
import StarRatingComponent from "react-star-rating-component";

class Subpage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      username: window.localStorage.getItem("username"),
      ISBN: this.props.location.state.ISBN,  //ISBN: from the props.history.push in Dashboard/Rec code, the ISBN of book clicked
      book: {} //detailed book info: title, author, publisher, year, image_url_l
    }

    //this.showMovies = this.showMovies.bind(this);
  }

  componentDidMount() {
    console.log("current book's ISBN: " + this.state.ISBN);
    console.log("current username: " + this.state.username);

    //Send an HTTP request to the server TO GET BOOK BY ISBN
    fetch(`http://localhost:8081/book/${this.state.ISBN}`, {
      method: 'GET' 
    })
      .then(res => res.json())
      .then(resArray => {
        if (!resArray) return;

        this.setState({
          book: resArray[0]
        });
      })
      .catch(err => console.log(err))	// Print the error if there is one.
  } 


    // Send an HTTP request to the server.
    // fetch("http://localhost:8081/genres", {
    //   method: 'GET' // The type of HTTP request.
    // })
    //   .then(res => res.json()) // Convert the response data to a JSON.
    //   .then(genreList => {
    //     if (!genreList) return;
    //     // Map each genreObj in genreList to an HTML element:
    //     // A button which triggers the showMovies function for each genre.
    //     let genreDivs = genreList.map((genreObj, i) =>
    //       <GenreButton id={"button-" + genreObj.genre} onClick={() => this.showMovies(genreObj.genre)} genre={genreObj.genre} />
    //     );

    //     // Set the state of the genres list to the value returned by the HTTP response from the server.
    //     this.setState({
    //       genres: genreDivs
    //     })
    //   })
    //   .catch(err => console.log(err))	// Print the error if there is one.


  onStarClick(nextValue, prevValue, name) {
    this.setState({rating: nextValue});
  }

  render() {  
    const {rating} = this.state;  

    return (
        <div className="Subpage">
        <PageNavbar active="" />
        <br/>
        {/* <!-- Page Content --> */}
        <div className="list-wrap">
            <div className="list pr-1">
              <div className="list-box bg-white px-3 pt-3">
                <h5 className="list-title mb-3">â‰¡ Book Information</h5>
        {/* <div className="jumbotron bg-white"> */}

          {/* <!-- Portfolio Item Heading --> */}
          {/* <!-- Portfolio Item Row --> */}
          <div className="list-row" >

            <div className="col-md-8 ">
              <img className="img-fluid" src={this.state.book.image_url_l} alt=""/>
            </div>

            <div className="col-md-4">
              <h1 className="my-4">{this.state.book.title}</h1>
              <div className="row ratingRow1">
                {/* <img id="starIcon" src={StarIcon}/>
                <h6>rating</h6> */}
                <p>Overall rating: </p>
                <StarRatingComponent 
                          name="rate1"
                          editing={false}
                          starCount={10}
                          value={rating}
                />
              </div>
              <div className="row ratingRow2">
                <p>Your rating: </p>
                <StarRatingComponent 
                          name="rate2"
                          editing={true}
                          starCount={10}
                          value={rating}
                          onStarClick={this.onStarClick.bind(this)}
                />
                <p>{rating}</p>
              </div>
              <button
                  className="btn btn-outline-secondary bg-primary text-white"
                  type="button"
                  id="button-rating"
                  onClick={this.submitRating}>
                  Submit Your Rating
              </button>
              {/* <h3 className="my-3">Book Description</h3> */}
              <ul>
                <li>ISBN: {this.state.ISBN}</li>
                <li>Author: {this.state.book.author}</li>
                <li>Publisher: {this.state.book.publisher}</li>
                <li>Year: {this.state.book.year}</li>
              </ul>
              <button
                  className="btn btn-outline-secondary bg-primary text-white"
                  type="button"
                  id="button-markBook"
                  onClick={this.addToBookshelf}>
                  Like it? Add to bookshelf
              </button>
            </div>

            <div className="row"> 

            </div>

          </div>
          {/* <!-- /.row --> */}  


          <h3 className="my-4">Related Books</h3>
          {/* <!-- Related Projects Row --> */}        
          <div className="list-row">

            <div className="col-md-3 col-sm-6 mb-4">
              <a href="#">
                    <img className="img-fluid" src="http://placehold.it/500x300" alt=""/>
                </a>
            </div>

            <div className="col-md-3 col-sm-6 mb-4">
              <a href="#">
                    <img className="img-fluid" src="http://placehold.it/500x300" alt=""/>
                  </a>
            </div>

            <div className="col-md-3 col-sm-6 mb-4">
              <a href="#">
                    <img className="img-fluid" src="http://placehold.it/500x300" alt="" />
                  </a>
            </div>

            <div className="col-md-3 col-sm-6 mb-4">
              <a href="#">
                    <img className="img-fluid" src="http://placehold.it/500x300" alt="" />
                  </a>
            </div>

          </div>
          {/* <!-- /.row --> */}


        {/* </div> */}
          {/* <!-- /.jumbotron --> */}
        </div>
        {/* <!-- /.subpage --> */}

        </div>
        </div>
        </div>
    );
  }
}
export default withRouter(Subpage);
