import PageNavbar from './PageNavbar';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from 'react';
import { withRouter } from 'react-router-dom'
import '../style/Recommendations.css';



class Recommendations extends React.Component {

  constructor(props) {
    super(props);
    this.state = {username: "",password:""};

	const eventsArr = [
		    //按钮点击事件
		    "onSignIn",
		    //user name输入改变
		    "onChangeName",
		    //password输入改变
		    "onChangePwd"
		];
        //一次性绑定所有事件，这样以后添加的事件只要添加到eventArr数组里面就好了
        eventsArr.map((ev) => {
            this[ev] = this[ev].bind(this);
            return "";
        });

  }

	onChangeName(e) {
        this.setState({ username: e.target.value });
        this.setState({ errorMsg: '' });
    }

    onChangePwd(e) {
        this.setState({ password: e.target.value });
    }

  onSignIn(event) {
    // Send an HTTP request to the server.
    fetch("http://localhost:8081/signin/username="+this.state.username+"&password="+this.state.password, {
       method: 'GET' // The type of HTTP request.
     })
       .then(res => res.json()) // Convert the response data to a JSON.
       .then(ifexist => {
        if (!ifexist) alert("Invalid account");
alert("Invalid account")
   	 	this.props.history.push({
                        pathname : '/dashboard',
                        state :{
                            username:this.state.username
                        }
                    });

       })
       .catch(err => console.log(err)) // Print the error if there is one.
    }

  render() {
    return (
    	<div className="Recommendations">

        <PageNavbar active="recommendations" />

        <br></br>

        <div className="small-middle-container">
      <form onSubmit={this.onSignIn}>


        <div class="form-group">
          <label for="exampleInputEmail1">Username</label>
          <input value={this.state.username} onChange={this.onChangeName} class="form-control"/>
        </div>

        <div class="form-group">
          <label for="exampleInputPassword1">Password</label>
          <input type="password" class="form-control" id="exampleInputPassword1" value={this.state.password}
           onChange={this.onChangePwd}/>
        </div>

        <button type="submit" class="btn btn-primary">Submit</button>


      </form></div>

      </div>  

    );
  }
}
export default withRouter(Recommendations);
